<!-- Вывод альбома id=1 через функцию index шаблон gal_index.tpl, ограничение 12 фотографий, постраничная навигация включена --><?php $GalleryModule->index(array ('section_include' => array (1), 'limit' = > 12, 'pagination' => true));?><!-- Вывод альбомов из родительского --><?php	if ($_GET['gal_id'] != '') {		$_GET['gal_id'] = (int)$_GET['gal_id'];		$GalleryModule->index(array ('section_include' => array ($_GET['gal_id']), 'limit' => 9, 'pagination' => true));	}else{		$GalleryModule->section(array ('parent' => 1));	}?><!-- Многоуровневый вывод фотогалереи --><?php	if ($_GET['gal_id'] != '') {		$_GET['gal_id'] = (int)$_GET['gal_id'];		$parent = sqlval("select id from foto_razdel where id_parent = {$_GET['gal_id']}");		if (empty($parent)) {			$GalleryModule->index(array ('section_include' => array ($_GET['gal_id']), 'limit' => 16, 'pagination' => true, 'template' => 'gal_view.tpl'));		} else {			$GalleryModule->section(array ('parent' => $_GET['gal_id'], 'template' => 'gal_child.tpl'));		}	} else {		$GalleryModule->section(array ('parent' => 1));	}?>