<!-- Код для стандартного вывода из модуля "Каталог товаров с опциями" может использоваться как одноязычная версия, так и мултиязычная (по умолчанию русский язык)--><?php	if ($_GET['item_id'] != '') {		$_GET['item_id'] = (int)$_GET['item_id'];		$PriceMultiModule->view(array ('id' => $_GET['item_id']));	} elseif ($_GET['cat_id'] != '') {		$_GET['cat_id'] = (int)$_GET['cat_id'];		$PriceMultiModule->index(array ('section_include' => array ($_GET['cat_id']), 'pagination' => true, 'limit' => 12));			} else {		$PriceMultiModule->section(array ());	}?><!-- Код для стандартного вывода из модуля "Каталог товаров с опциями" для мультиязычной версии (en язык). Шаблоны дублируются и добавляется преставка "_en"--><?php	if ($_GET['item_id'] != '') {		$_GET['item_id'] = (int)$_GET['item_id'];		$PriceMultiModule->view(array ('id' => $_GET['item_id'], 'lang'=>'en'));	} elseif ($_GET['cat_id'] != '') {		$_GET['cat_id'] = (int)$_GET['cat_id'];		$PriceMultiModule->index(array ('section_include' => array ($_GET['cat_id']), 'pagination' => true, 'limit' => 12, 'lang'=>'en'));			} else {		$PriceMultiModule->section_index(array ('lang'=>'en'));	}?>